import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useAuth } from '../AuthContext';
import './History.css';

const History = () => {
    const { user } = useAuth();
    const [history, setHistory] = useState([]);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchHistory = async () => {
            if (user) {
                try {
                    const response = await axios.get(`http://localhost:8080/api/videos/watched-videos/${user.username}`);
                    setHistory(response.data);
                } catch (error) {
                    setError('Error fetching watch history.');
                    console.error(error);
                }
            }
        };
        fetchHistory();
    }, [user]);

    return (
        <div className="history-container">
            <h1>Watch History</h1>
            {error && <div className="error">{error}</div>}
            {history.length === 0 ? (
                <p>No videos watched yet.</p>
            ) : (
                <ul className="history-list">
                    {history.map((item, index) => (
                        <li key={index} className="history-item">
                            <a href={`/Watch/${item.video.id}`}>
                                {item.video.title}
                            </a>
                            <span className="watched-at">Watched at: {new Date(item.watchedAt).toLocaleString()}</span>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default History;
